const m=t=>{const e=t.getDate().toString().padStart(2,"0"),n=(t.getMonth()+1).toString().padStart(2,"0"),s=t.getFullYear();return`${e}-${n}-${s}`},d=t=>{const[e,n,s]=t.split("-").map(Number);return new Date(s,n-1,e).toLocaleDateString("es-ES",{weekday:"long",day:"numeric",month:"long",year:"numeric"})},l=t=>{const[e,n]=t.split(":").map(Number);return e*60+n},p=(t,e,n)=>{const[s,o]=t.split(":").map(Number),[a,r]=e.split(":").map(Number),i=s*60+o,u=a*60+r;return i+n<=u};export{d as a,m as f,p as i,l as t};
