/* build-hook-start *//*00001*/try { require('c:\\Users\\Alejandro\\.vscode\\extensions\\wallabyjs.console-ninja-1.0.441\\out\\buildHook\\index.js').default({tool: 'vite', checkSum: '20367f01575221045763d0AxgPSAELBwVUVFcFAAACBQcLXQAA', mode: 'build'}); } catch(cjsError) { try { import('file:///c:/Users/Alejandro/.vscode/extensions/wallabyjs.console-ninja-1.0.441/out/buildHook/index.js').then(m => m.default.default({tool: 'vite', checkSum: '20367f01575221045763d0AxgPSAELBwVUVFcFAAACBQcLXQAA', mode: 'build'})).catch(esmError => {}) } catch(esmError) {}}/* build-hook-end */

const path = require('path')
const chalk = require('chalk')
const argv = require('minimist')(process.argv.slice(2))
const getIPv4AddressList = require('../dist/utils').getIPv4AddressList

console.log(chalk.cyan(`vite v${require('../package.json').version}`))

if (argv._[0] === 'build') {
  console.log(chalk.yellow('Building for production...'))
  require('../dist')
    .build({
      ...argv,
      cdn: argv.cdn === 'false' ? false : argv.cdn
    })
    .catch((err) => {
      console.error(chalk.red(`[vite] Build errored out.`))
      console.log(err)
    })
} else {
  const server = require('../dist').createServer(argv)

  let port = argv.port || 3000

  server.on('error', (e) => {
    if (e.code === 'EADDRINUSE') {
      console.log(`Port ${port} is in use, trying another one...`)
      setTimeout(() => {
        server.close()
        server.listen(++port)
      }, 100)
    } else {
      console.error(chalk.red(`[vite] server error:`))
      console.error(e)
    }
  })

  server.on('listening', () => {
    console.log(`Dev server running at:`)
    getIPv4AddressList().forEach((ip) => {
      console.log(`  > http://${ip}:${port}`)
    })
    console.log(' ')
  })

  server.listen(port)
}
